mapper.select:
SELECT info.id as inn_id,info.name,tid.mobile,tid.user_code , allRoom.totalRoomNum,p_.liveRoomNum,p_.priceSum,p_.cdate from tomato_inn info LEFT JOIN tomato_inn_admin tid ON tid.inn_id = info.id AND tid.status=2 and tid ISNULL LEFT JOIN ( SELECT count(o.id) AS totalRoomNum,inn_id as innId from tomato_inn_room o where o.status=1 GROUP BY o.inn_id ) allRoom ON allRoom.innId = info.id LEFT JOIN tomato_inn_main_order imo ON imo.inn_id = info.id LEFT JOIN ( SELECT count(o.id) as liveRoomNum,sum(o.price) as priceSum, o.inn_id ,o.cdate from tomato_inn_sub_order_price o LEFT JOIN tomato_inn_main_order imo ON imo.id = o.main_id GROUP BY o.inn_id ,o.cdate ) p_ ON p_.inn_id = info.id where imo.order_status>0 and p_.liveRoomNum NOTNULL and p_.priceSum NOTNULL
and p_.cdate='${value}'
GROUP BY info.id, allRoom.totalRoomNum,p_.liveRoomNum,p_.priceSum,p_.cdate,info.name,tid.mobile,tid.user_code;
